mapper.select:
SELECT `uid`, `title`
FROM board_kind
ORDER BY uid ASC;

mapper.insert:
INSERT INTO board(`board_kind_uid`, `user_uid`, `title`, `body`)
VALUES (#{board_kind_uid}, #{user_uid}, #{title}, #{body});

    SELECT LAST_INSERT_ID()

mapper.select:
SELECT COUNT(*)
FROM board b
WHERE board_kind_uid = #{board_kind_uid};

mapper.update:
UPDATE board
title=#{title}, body=#{body},
WHERE uid = #{uid};
